mapper.select:
SELECT c.* FROM ( SELECT a.id, a.channel_id AS channelId, a.DAY_QUOTA_AMOUNT as dayQuotaAmount, a.merch_id AS merchId, a.UP_MERCH_NO AS upMerchNo, a.UP_MERCH_KEY AS upMerchKey, a.MIN_PRICE AS minPrice, a.MAX_PRICE AS maxPrice, a.PAY_TYPE AS payType, a.REMARK AS remark, a.REMARKS AS remarks, a.USER_ID AS userId, a.NAME AS NAME, a.BANK_NO AS bankNo, a.cardIdx AS cardIdx, a.BANK_MARK AS bankMark, a.TYPE AS TYPE, a.LAST_ORDER_TIME AS lastOrderTime FROM VC_A1_CHANNEL_SUBNO a LEFT JOIN vc_a1_channel b ON a.`channel_id`=b.id WHERE a.del_flag = 0 and a.status =1 and b.`STATUS`=1 AND b.`DEL_FLAG`=0 AND b.`SUBNO_STATUS`=1 and IFNULL(a.DAY_TRA_AMOUNT,0) <= IFNULL(a.DAY_QUOTA_AMOUNT,0) and IFNULL(a.TRA_AMOUNT,0) <= IFNULL(a.QUOTA_AMOUNT,0)
and b.CHANNEL_SOURCE = #{channelSource}
and IFNULL(a.MIN_PRICE,0) <= #{traAmount} and IFNULL(a.MAX_PRICE,0) >= #{traAmount}
and a.UP_MERCH_NO = #{upMerchNo}
and a.channel_id = #{channelId}
and a.merch_id = #{merchId}
and (IFNULL(a.PAY_TYPE,0) = #{payType} or IFNULL(PAY_TYPE,0) = 0)
ORDER BY LAST_ORDER_TIME asc ,DAY_TRA_AMOUNT asc LIMIT 10 ) c ORDER BY RAND()

mapper.update:
UPDATE VC_A1_CHANNEL_SUBNO a SET DAY_TRA_AMOUNT=( CASE WHEN LAST_DAY < CURRENT_DATE() THEN #{actualAmount} ELSE IFNULL(DAY_TRA_AMOUNT,0)+#{actualAmount} END ), LAST_DAY = current_date(), TRA_AMOUNT=IFNULL(TRA_AMOUNT,0)+#{actualAmount}, update_date = sysdate() , LAST_ORDER_TIME = sysdate() WHERE up_merch_no=#{upMerchNo} and del_flag = 0
and channel_id = #{channelId}
and merch_id = #{merchId}

UPDATE VC_A1_CHANNEL_SUBNO a SET LAST_ORDER_TIME = sysdate(),update_date = sysdate()  
WHERE  up_merch_no=#{upMerchNo} and del_flag = 0 

	AND id = #{id}
