mapper.select:
SELECT id, channel_id as channelId, merch_id as merchId, UP_MERCH_NO as upMerchNo, UP_MERCH_KEY as upMerchKey, MIN_PRICE as minPrice, MAX_PRICE as maxPrice, PAY_TYPE as payType, REMARK as remark, REMARKS as remarks, USER_ID as userId, name as name, BANK_NO as bankNo, cardIdx as cardIdx, BANK_MARK as bankMark, TYPE as type, LAST_ORDER_TIME as lastOrderTime FROM VC_A1_CHANNEL_SUBNO WHERE del_flag = 0 and status =1 and IFNULL(DAY_TRA_AMOUNT,0) <= IFNULL(DAY_QUOTA_AMOUNT,0) and IFNULL(TRA_AMOUNT,0) <= IFNULL(QUOTA_AMOUNT,0)
and IFNULL(MIN_PRICE,0) <= #{traAmount} and IFNULL(MAX_PRICE,0) >= #{traAmount}
and UP_MERCH_NO = #{upMerchNo}
and channel_id = #{channelId}
and merch_id = #{merchId}
and (IFNULL(PAY_TYPE,0) = #{payType} or IFNULL(PAY_TYPE,0) = 0)
ORDER BY LAST_ORDER_TIME asc ,DAY_TRA_AMOUNT asc LIMIT 10

mapper.update:
UPDATE VC_A1_CHANNEL_SUBNO a SET DAY_TRA_AMOUNT=( CASE WHEN LAST_DAY < CURRENT_DATE() THEN #{actualAmount} ELSE IFNULL(DAY_TRA_AMOUNT,0)+#{actualAmount} END ), LAST_DAY = current_date(), TRA_AMOUNT=IFNULL(TRA_AMOUNT,0)+#{actualAmount}, update_date = sysdate() , LAST_ORDER_TIME = sysdate() WHERE 1=1
and channel_id = #{channelId}
and merch_id = #{merchId}
and up_merch_no=#{upMerchNo} and del_flag = 0

UPDATE VC_A1_CHANNEL_SUBNO a SET LAST_ORDER_TIME = sysdate(),update_date = sysdate()  
WHERE  up_merch_no=#{upMerchNo} and del_flag = 0 

	AND id = #{id}
