mapper.select:
SELECT

FROM
vc_online_order a
WHERE
a.del_flag = 0

	and a.SMSTRXID LIKE CONCAT('',#{smstrxid},'%')


	AND a.order_no = #{orderNo}


	and a.up_merch_no = #{upMerchNo}


	and a.p_order = #{pOrder}


	and a.tra_amount BETWEEN #{traAmount} - 1 and #{traAmount} + 1

SELECT

FROM vc_online_order a
WHERE a.del_flag = 0
	AND a.pay_code =#{traAmount}
	AND a.up_merch_no = #{upMerchNo}
	AND a.status = 1
	AND a.p_order != #{pOrder}
	AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}

mapper.update:
UPDATE vc_online_order SET
order_des = #{orderDes},
update_date = sysdate() WHERE order_no = #{orderNo}

mapper.select:
SELECT a.id AS "id", a.merch_id AS "merchId", a.merch_no AS "merchNo", a.merch_name AS "merchName", a.order_no AS "orderNo", a.tra_amount AS "traAmount", a.actual_amount AS "actualAmount", a.pay_code AS "payCode", a.pay_type AS "payType", a.pay_source AS "paySource", a.pay_mode AS "payMode", a.pay_key AS "payKey", a.status AS "status", a.c_order AS "cOrder", a.c_notify_url AS "cNotifyUrl", a.c_sign AS "cSign", a.p_order AS "pOrder", a.p_sign AS "pSign", a.p_rescode AS "pRescode", a.order_des AS "orderDes", a.create_date AS "createDate", a.remarks AS "remarks", a.channel_id AS "channelId", a.up_merch_no as upMerchNo, a.up_merch_key as upMerchKey FROM vc_online_order a WHERE a.del_flag = 0 AND a.status =1
AND a.order_no = #{orderNo}
AND a.pay_source IN #{item}
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}

mapper.update:
UPDATE vc_online_order SET
C_NOTIFY_RESULT = #{cNotifyResult},
C_NOTIFY_NUM = IFNULL(C_NOTIFY_NUM,0) +1,update_date = sysdate() WHERE order_no = #{orderNo}

mapper.select:
SELECT a.id
FROM  vc_online_order a
WHERE   a.p_order = #{pOrder}
	AND a.up_merch_no = #{upMerchNo}
	AND a.status = 4
	AND a.tra_amount BETWEEN #{traAmount} - 1 and #{traAmount} + 1

SELECT a.id
FROM  VC_ONLINE_ORDER_REPAY a
WHERE   a.p_order = #{pOrder}
